mapper.select:
SELECT s.id 
FROM stock s
JOIN office o ON s.office_id = o.id
JOIN menu m ON s.menu_id = m.id
WHERE o.office_name = #{officeName}
AND m.menuName = #{menuName}

mapper.update:
        UPDATE orders
        SET
            `status` = #{status},
			last_update = #{lastUpdate}
        WHERE id = #{id}

UPDATE orders
SET 
    status = CASE

            WHEN id = #{item.id} THEN #{item.status}

    END,
    last_update = CASE

            WHEN id = #{item.id} THEN #{item.lastUpdate}

    END
WHERE id IN

    #{item.id}
