mapper.select:
SELECT id, inventory_Id, menu_Id, status, quantity, last_Update
FROM orders
WHERE id = #{id}

SELECT id, inventory_Id, menu_Id, status, quantity, last_Update
FROM orders
WHERE inventory_Id = #{inventoryId}
AND menu_Id = #{menuId}
AND quantity = #{quantity}

mapper.update:
UPDATE orders
SET
	status = #{status}
WHERE id = #{id}
